const article=document.getElementsByClassName("article-item");let wordCount=0;for(let i=0;i<article.length;i++){wordCount+=article[i].textContent.trim().split(/\s+/).length}estReadTime=Math.round(wordCount/200);if(estReadTime<1){estReadTime=1}document.getElementsByClassName("word-count")[0].textContent=estReadTime+" minute read.";